import React from 'react';
import { View, Text, StyleSheet, Button, ScrollView } from 'react-native';

const Wishlist = () => {
  // Sample wishlist items (to be replaced with database items)
  const wishlistItems = [
    { name: 'Apple iPhone 14, 128gb, Blue', price: '$999.00', status: 'In Stock' },
    { name: 'Nintendo Switch Pro Wireless Game Controller - Black', price: '$70.00', status: 'Out of Stock' }
  ];

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerText}>MY WISHLIST</Text>
      </View>

      <View style={styles.listHeader}>
        <Text style={styles.column}>PRODUCT NAME</Text>
        <Text style={styles.column}>PRICE</Text>
        <Text style={styles.column}>STOCK STATUS</Text>
      </View>

      {wishlistItems.map((item, index) => (
        <View key={index} style={styles.listItem}>
          <Text style={styles.itemName}>{item.name}</Text>
          <Text style={styles.itemPrice}>{item.price}</Text>
          <Text style={styles.itemStatus}>{item.status}</Text>
          <Button 
            title="Add to cart"
            color={item.status === 'In Stock' ? '#2B2438' : '#888888'} // Different color for stock status
            disabled={item.status !== 'In Stock'}
          />
        </View>
      ))}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flexGrow: 1,
    padding: 20,
    backgroundColor: '#E5E5E5'
  },
  header: {
    alignItems: 'center',
    marginBottom: 20,
  },
  headerText: {
    fontSize: 30,
    fontWeight: '700',
    textAlign: 'center',
    color: '#000',
  },
  listHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 10,
    borderBottomWidth: 1,
    paddingBottom: 10,
  },
  column: {
    fontWeight: '700',
    fontSize: 18,
    color: '#000',
    textAlign: 'center',
    flex: 1,
  },
  listItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingVertical: 15,
    borderBottomWidth: 1,
    borderColor: '#ccc',
  },
  itemName: {
    fontSize: 14,
    textAlign: 'center',
    flex: 2,
    color: '#000',
  },
  itemPrice: {
    fontSize: 14,
    textAlign: 'center',
    flex: 1,
    color: '#000',
  },
  itemStatus: {
    fontSize: 14,
    textAlign: 'center',
    flex: 1,
    color: '#000',
  },
});

export default Wishlist;
