import React, { useState } from "react";
import googleLogo from '../assets/images/google.png';
import headerImage from '../assets/images/logo.png'; // Add your header image path here


const SignupPage = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    password: "",
    confirmPassword: ""
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic
    console.log("Sign Up form data:", formData);
  };

  return (
    <div style={styles.container}>
      {/* Left Section for Google Signup */}
      <div style={styles.leftSection}>
        <img src={headerImage} alt="Logo" style={styles.logo} />
        <h1 style={styles.title}>SIGN UP WITH GOOGLE</h1>
        <button style={styles.googleButton}>
          <img src={googleLogo}
            alt="Google Logo"
            style={styles.googleIcon}
          />
          <span>Sign Up with Google</span>
        </button>
      </div>

      {/* Right Section for Form Inputs */}
      <div style={styles.rightSection}>
        <h2 style={styles.formTitle}>SIGN UP</h2>
        <form onSubmit={handleSubmit} style={styles.form}>
          <input
            type="text"
            name="fullName"
            placeholder="Full Name"
            value={formData.fullName}
            onChange={handleChange}
            style={styles.input}
          />
          <input
            type="email"
            name="email"
            placeholder="Enter Email"
            value={formData.email}
            onChange={handleChange}
            style={styles.input}
          />
          <input
            type="password"
            name="password"
            placeholder="Enter Password"
            value={formData.password}
            onChange={handleChange}
            style={styles.input}
          />
          <input
            type="password"
            name="confirmPassword"
            placeholder="Confirm Password"
            value={formData.confirmPassword}
            onChange={handleChange}
            style={styles.input}
          />
          <button type="submit" style={styles.submitButton}>Sign Up</button>
        </form>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "row",
    height: "100vh",
    backgroundColor: "#2A2732"
  },
  leftSection: {
    flex: 1,
    backgroundColor: "#2A2732",
    color: "#fff",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center"
  },
  logo: {
    width: "150px",
    marginBottom: "20px"
  },
  title: {
    fontSize: "32px",
    fontWeight: "bold",
    marginBottom: "30px"
  },
  googleButton: {
    backgroundColor: "#fff",
    padding: "10px 20px",
    display: "flex",
    alignItems: "center",
    borderRadius: "5px",
    cursor: "pointer",
    border: "none",
    outline: "none"
  },
  googleIcon: {
    width: "20px",
    marginRight: "10px"
  },
  rightSection: {
    flex: 1,
    backgroundColor: "#E5E5E5",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center"
  },
  formTitle: {
    fontSize: "28px",
    fontWeight: "bold",
    marginBottom: "20px"
  },
  form: {
    display: "flex",
    flexDirection: "column",
    width: "80%",
    maxWidth: "400px"
  },
  input: {
    padding: "15px",
    margin: "10px 0",
    borderRadius: "5px",
    border: "1px solid #2A2732",
    fontSize: "16px"
  },
  submitButton: {
    padding: "15px",
    backgroundColor: "#2A2732",
    color: "#fff",
    fontSize: "16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer"
  }
};

export default SignupPage;
